mapper.select:
SELECT 
	id, ip, login_name, login_password, envir_name, system_name, is_admin
FROM envir_info
WHERE is_admin=#{isAdmin}
AND	ip=#{ip}

mapper.update:
UPDATE envir_info
ip=#{ip}, login_name=#{loginName}, login_password=#{loginPassword}, envir_name=#{envirName}, system_name=#{systemName}, is_admin=#{isAdmin}
WHERE id=#{id}
